mapper.select:
SELECT id, title, writer, views, write_date, reply_count
from post
where board_id = #{boardId}

mapper.update:
update post
set title=#{title},
content=#{content},
writer=#{writer}
where id = #{id}

update post
set reply_count = reply_count + 1
where id = #{id}

update post
set reply_count = reply_count - 1
where id = #{id}

update post
set views = views + 1
where id = #{id}
